const initialState ={

cart:{
    content:[]
}
    
}

const mainReducer=(state:initialState, action)=>{


switch(action.type){
    case 'ADD_TO_FAVORITE': return {
        ...state, cart:{...state.cart, content:[...state.cart.content, action.payload]}
    }
}

}


export default mainReducer
